mapper.select:
		select *
		from (select question_board.*,rownum as rnum
       			 from question_board


       			 		q_category = #{q_category}


       			 		q_category = #{q_category}


       			 order by q_num desc)



						RNUM BETWEEN #{start_row} AND #{end_row}

		select *
		from (select question_board.*,rownum as rnum
			  from question_board


       			 		q_category = #{q_category}


       			 		q_category = #{q_category}


			  order by q_num desc)

			id = #{id}


					and	RNUM BETWEEN #{start_row} AND #{end_row}

mapper.update:
update question_board set q_title = #{q_title}, q_content = #{q_content}, q_category = #{q_category}, q_passwd = #{q_passwd},
q_image1 = #{q_image1}, q_image2 = #{q_image2}, q_image3 = #{q_image3}
where q_num = #{q_num}
