mapper.update:
UPDATE question_board
SET
q_reply = #{q_reply}
WHERE q_num = #{q_num}

mapper.select:
		SELECT *
		FROM(select
			 list.*, rownum as rnum
			 from (select *
            	   from question_board
             	   order by q_num desc) list


					q_category = #{q_category}


		)

			rnum between #{start_row} and #{end_row}
