mapper.select:
select * from delivery
where id=#{id}
order by o_serialnum desc

mapper.insert:
		insert INTO DELIVERY(
			o_serialnum
			, p_num
			, id
			, o_date
			, o_address
			, o_tel
			, o_quantity
			, o_totalprice
			, o_price
			, o_num
			, o_reciever
			, o_reciever_number
			, o_delivery
			, o_image
			, o_product
			, o_paymethod
			, o_status
		)VALUES(
				delivery_seq.nextval
				,#{p_num}
				,#{id}
				,sysdate
   	      ,#{o_address}
   	      ,#{o_tel}
   	      ,#{o_quantity}
   	      ,#{o_totalprice}
   	      ,#{o_price}
   	      ,sysdate||#{id}
   	      ,#{o_reciever}
   	      ,#{o_reciever_number}
   	      ,#{o_delivery}
   	      ,#{o_image}
   	      ,#{o_product}
   	      ,#{o_paymethod}
   	      ,#{o_status}
	)
